/*
 * 스레드 풀이 잘 구현되었는지 검증하기 위해 작성된 메인 함수이다.
 */
int main(void)
{
    int i, j, num[NTASK];
    struct timespec req;
    pthread_pool_t pool1, pool2, pool3, pool4;

    /*
     * 스레드풀의 한계를 검사한다.
     */
    if (pthread_pool_init(&pool1, 65536, 16) == POOL_FAIL)
        printf("pthreaad_pool_init(): 일꾼 스레드 최대 수 초과.....PASSED\n");
    else {
        printf("Error: 일꾼 스레드 갯수 검사 오류\n");
        return 1;
    }
    if (pthread_pool_init(&pool1, 16, 65536) == POOL_FAIL)
        printf("pthreaad_pool_init(): 대기열 최대 용량 초과.....PASSED\n");
    else {
        printf("Error: 대기열 용량 검사 오류\n");
        return 1;
    }
    
    /*
     * 스레드 풀을 사용하기 전에 먼저 초기화한다.
     */
    if (pthread_pool_init(&pool1, 16, 64)) {
        printf("Error: 스레드풀 초기화 오류\n");
        return 1;
    }
    printf("pthread_pool_init(): 완료......PASSED\n");
    /*
     * 스레드 풀이 정상으로 종료되는지 검사한다.
     */
    if (pthread_pool_shutdown(&pool1)) {
        printf("Error: 스레드풀 종료 오류\n");
        return 1;
    }
    printf("pthread_pool_shutdown(): 완료......PASSED\n");

    /*
     * 3개의 일꾼 스레드와 대기열의 크기가 10개인 스레드 풀을 가동한다.
     */
    pthread_pool_init(&pool1, 3, 10);
    /*
     * 함수 numberX()를 실행하기 위해 NTASK 갯수 만큼 스레드 풀에 요청한다.
     * 스레드 풀의 대기열은 길이가 10개를 넘을 수 없어서 요청을 아무리 빨리 처리해도 받을 수 없는
     * 요청이 발생할 수 있다. 대기열이 차서 요청이 거절되면 대기열이 찼다는 메시지를 출력한다.
     */
    for (i = 0; i < NTASK; ++i) {
        num[i] = i;
        if (pthread_pool_submit(&pool1, number1, num+i, POOL_NOWAIT))
            printf("%d: Queue is full\n", i);
    }
    /*
     * POOL_WAIT 플래그를 사용하여 위 과정을 반복한다.
     * 대기열에 빈 자리가 날 때까지 기다리기 때문에 작업 요청은 항상 성공한다.
     */
    for (i = 0; i < NTASK; ++i) {
        num[i] = i;
        if (pthread_pool_submit(&pool1, number2, num+i, POOL_WAIT)) {
            printf("Error: POOL_WAIT 옵션 오류\n");
            return 1;
        }
    }
    /*
     * 스레드풀을 종료한다. 실행이 안 된 채로 대기열에 남아 있는 작업이 있을 수 있다.
     */
    pthread_pool_shutdown(&pool1);
    printf("......PASSED\n");
    
    /*
     * 8개의 일꾼 스레드와 대기열의 크기가 16개인 스레드 풀을 가동한다.
     */
    pthread_pool_init(&pool1, 8, 16);
    /*
     * ASCII 문자로 된 어떤 얼굴을 출력하도록 스레드풀에 요청한다.
     */
    pthread_pool_submit(&pool1, face, NULL, POOL_WAIT);
    /*
     * 실행의 흐름이 얼굴을 출력하는 일꾼 스레드로 넘어갈 수 있게 부모 스레드는 잠깐 쉰다.
     * 부모 스레드가 쉬는 동안 일꾼 스레드가 얼굴을 출력하기 시작한다.
     */
    req.tv_sec = 0;
    req.tv_nsec = 1L;
    nanosleep(&req, NULL);
    /*
     * 스레드 풀을 종료한다. 진행중인 얼굴의 뒷 부분이 잘리지 않게 설계하는 것이 좋다.
     */
    pthread_pool_shutdown(&pool1);
    printf("얼굴이 중간에 잘리지 않게 설계하는 것이 좋다......PASSED\n");
    
    /*
     * 스레드풀 두 개를 동시에 운영한다. 출력은 앞에 있는 검증과 유사하다.
     */
    pthread_pool_init(&pool1, 3, 10);
    pthread_pool_init(&pool2, 5, 20);
    for (i = 0; i < NTASK; ++i) {
        num[i] = i;
        if (pthread_pool_submit(&pool1, number1, num+i, POOL_WAIT)) {
            printf("Error: POOL_WAIT 옵션 오류\n");
            return 1;
        }
    }
    for (i = 0; i < NTASK; ++i) {
        num[i] = i;
        if (pthread_pool_submit(&pool2, number2, num+i, POOL_WAIT)) {
            printf("Error: POOL_WAIT 옵션 오류\n");
            return 1;
        }
    }
    /*
     * 스레드풀 하나를 종료한다. 실행이 안 된 채로 대기열에 남아 있는 작업이 있을 수 있다.
     */
    pthread_pool_shutdown(&pool2);
    printf("......PASSED\n");

    /*
     * 스레드풀이 큰 부하에도 오류 없이 작동하는지 검증한다.
     * 락을 사용하는 스레드를 철회하면 race condition이 발생하여 교착상태에 빠지기 쉽다.
     * 스레드를 중간에 철회하는 방식보다 자연스럽게 종료시키는 방식으로 설계하는 것이 좋다.
     */
    srand(time(NULL));
    for (i = 0; i < NLOOP; ++i) {
        /*
         * 스레드풀 세 개를 무작위로 생성한다.
         */
        pthread_pool_init(&pool2, rand()%POOL_MAXBSIZE+1, rand()%POOL_MAXQSIZE+1);
        pthread_pool_init(&pool3, rand()%POOL_MAXBSIZE+1, rand()%POOL_MAXQSIZE+1);
        pthread_pool_init(&pool4, rand()%POOL_MAXBSIZE+1, rand()%POOL_MAXQSIZE+1);
        /*
         * 세 개에 스레드풀에 점을 출력하는 작업을 요청한다. flag의 선택은 무작위로 한다.
         */
        for (j = 0; j < NLOOP; ++j) {
            pthread_pool_submit(&pool2, dot, NULL, rand()%2?POOL_WAIT:POOL_NOWAIT);
            pthread_pool_submit(&pool3, dot, NULL, rand()%2?POOL_WAIT:POOL_NOWAIT);
            pthread_pool_submit(&pool4, dot, NULL, rand()%2?POOL_WAIT:POOL_NOWAIT);
        }
        /*
         * 앞에서 만들었던 스레드풀 1번은 지속적으로 사용한다.
         * 나머지 스레드풀을 종료한다. 실행이 안 된 채로 대기열에 남아 있는 작업이 있을 수 있다.
         */
        pthread_pool_submit(&pool1, number3, &i, POOL_NOWAIT);
        pthread_pool_shutdown(&pool2);
        pthread_pool_shutdown(&pool3);
        pthread_pool_shutdown(&pool4);
    }
    /*
     * 마지막으로 스레드풀 1번을 종료하고 마친다.
     */
    pthread_pool_shutdown(&pool1);
    printf("......PASSED\n");
    return 0;
}
